fragmentCode.frag:
#version 330
layout(location = 0) out vec4 glFragColor;
in vec4 glFragCoord; // custom version of gl_FragCoord, used for tiled rendering.
#define SYNTHCLIPSE_ONLY
uniform vec3 iResolution;           // viewport resolution (in pixels)
uniform float iGlobalTime;           // shader playback time (in seconds)
uniform float iChannelTime[4];       // channel playback time (in seconds)
uniform vec3 iChannelResolution[4]; // channel resolution (in pixels)
uniform vec4 iMouse;                // mouse pixel coords. xy: current (if MLB down), zw: click
uniform vec4 iDate;                 // (year, month, day, time in seconds)
uniform sampler2D depthTex;			//! depthmap[]

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec4 col_depth = texture(depthTex, uv);

	fragColor = col_depth; //vec4(uv, 0.5 + 0.5 * sin(iGlobalTime), 1.0);
}

void main() {
    vec4 color;
    mainImage(color, glFragCoord.xy);
    glFragColor = color;
}

vertexCode.vert:
#version 330
#define SYNTHCLIPSE_ONLY
layout(location = 0) in vec3 VertexPosition;

// Matrix used for tiled rendering:
uniform mat4 TileMatrix = mat4(1.0);
uniform vec2 resolution;

// custom version of gl_FragCoord, used for tiled rendering.
out vec4 glFragCoord;

void main() {
	vec2 v = VertexPosition.xy;
	v.x = v.x * TileMatrix[0][0] + TileMatrix[3][0];
	v.y = v.y * TileMatrix[1][1] + TileMatrix[3][1];
	v = v * 0.5 + 0.5;
	glFragCoord.xy = v * resolution;

	gl_Position = vec4(VertexPosition, 1.0);
}
