fragmentCode.frag:
#version 400
#define SYNTHCLIPSE_ONLY
uniform vec3 iResolution;           // viewport resolution (in pixels)
uniform float iGlobalTime;           // shader playback time (in seconds)
uniform float iChannelTime[4];       // channel playback time (in seconds)
uniform vec3 iChannelResolution[4]; // channel resolution (in pixels)
uniform vec4 iMouse;                // mouse pixel coords. xy: current (if MLB down), zw: click
uniform vec4 iDate;                 // (year, month, day, time in seconds)
uniform float Jitter; //! slider[0,0.5,2]
uniform sampler2D iChannel0; 			//! texture["Shadertoy/res/tex16.png"]
uniform bool Julia; //! checkbox[false]

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	// TODO: Add your code here.

	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 ref_color = texture(iChannel0, uv);
	float add_color;
	if(Julia) {
		add_color = ref_color.r;
	}else {
		add_color = ref_color.g;
	}
	fragColor = vec4(uv, add_color + 0.5 + 0.5 * sin(iGlobalTime) * Jitter, 1.0);
}

void main() {
	vec4 color;
	mainImage(color, gl_FragCoord.xy);

	color.w = 1.0;
	gl_FragColor = color;
}

